import json
import ast

def convert_to_json_array(input_string):
    """
    Converts a string representation of a list or a plain string into a proper JSON array.
    - If it's a list (e.g. "['A', 'B']"), it parses and returns it as a JSON array.
    - If it's a plain string (e.g. Demo ), wraps it in a JSON array.
    - If empty, returns an empty array.
    """
    if not input_string.strip():
        return json.dumps([], indent=4)

    try:
        # Try evaluating the string as a Python literal
        parsed = ast.literal_eval(input_string)

        # If it's a list, return as JSON array
        if isinstance(parsed, list):
            return json.dumps(parsed, indent=4)

        # Otherwise, wrap the value in a list
        return json.dumps([parsed], indent=4)

    except (SyntaxError, ValueError):
        # Fallback: treat it as a plain string
        return json.dumps([input_string.strip()], indent=4)
# Example input
input_string = '$workflow.definition_workflow_02NZIZHMOVRTN5J2ZX0Lm4ZXS0guLZW85hh.input.variable_workflow_02NZIZHN4HSCG4qJCsL1e8Dy3sqmuOhswGR$'

# Convert and print the JSON array
json_output = convert_to_json_array(input_string)
print(json_output)
